INSERT INTO `wp_posts` VALUES("846", "1", "2020-11-03 21:37:37", "2020-11-03 14:37:37", "<h2 class=\"uppercase title-heading\" style=\"text-align: center;\">SHOWROOM BÌNH THẠNH</h2>\n<div class=\"col-md-12\">\n<div class=\"row content-page\">\n<div class=\"box padding\">\n\nShowroom Bình Thạnh hiện đang tọa lạc tại địa chỉ 246F, Bạch Đằng, Phường 24, Quận Bình Thạnh (Gần chợ Bà Chiểu và Ngã Tư Hàng Xanh).\n\nTại đây, chúng tôi hiện đang trưng bày rất nhiều sản phẩm tủ quần áo, giường ngủ, tủ giày, kệ tivi, bàn trang điểm.....\n\nQuý khách có thể đến đây để mua hàng sẵn có, hoặc có thể đặt hàng theo yêu cầu của quý khách.\n\nToàn bộ sản phẩm đều do xưởng mộc Thịnh Phát của chúng tôi làm ra.\n\n<img src=\"//file.hstatic.net/1000190420/file/img_4998_grande.jpg\" />\n\n<img src=\"//file.hstatic.net/1000190420/file/watermarked-img_1539_grande.jpg\" />\n\n<img src=\"//file.hstatic.net/1000190420/file/watermarked-img_1540_grande.jpg\" />\n\n<img src=\"//file.hstatic.net/1000190420/file/watermarked-img_1542_grande.jpg\" />\n\n<img src=\"//file.hstatic.net/1000190420/file/watermarked-img_1536_grande.jpg\" />\n\n</div>\n</div>\n</div>", "SHOWROOM_01", "", "inherit", "closed", "closed", "", "293-autosave-v1", "", "", "2020-11-03 21:37:37", "2020-11-03 14:37:37", "", "293", "http://noithat.lethach.com/khong-phan-loai/293-autosave-v1/", "0", "revision", "", "0");
