INSERT INTO `wp_wc_admin_notes` VALUES("1", "wc-admin-wc-helper-connection", "info", "en_US", "Connect to WooCommerce.com", "Connect to get important product notifications and updates.", "{}", "unactioned", "woocommerce-admin", "2020-10-26 16:43:27", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("2", "wc-admin-onboarding-profiler-reminder", "update", "en_US", "Welcome to WooCommerce! Set up your store and start selling", "We\'re here to help you going through the most important steps to get your store up and running.", "{}", "unactioned", "woocommerce-admin", "2020-10-26 16:43:27", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("3", "ecomm-need-help-setting-up-your-store", "info", "en_US", "Need help setting up your Store?", "Schedule a free 30-min <a href=\"https://wordpress.com/support/concierge-support/\">quick start session</a> and get help from our specialists. We’re happy to walk through setup steps, show you around the WordPress.com dashboard, troubleshoot any issues you may have, and help you the find the features you need to accomplish your goals for your site.", "{}", "pending", "woocommerce.com", "2020-08-08 15:44:41", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("4", "woocommerce-services", "info", "en_US", "WooCommerce Shipping & Tax", "WooCommerce Shipping &amp; Tax helps get your store “ready to sell” as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.", "{}", "pending", "woocommerce.com", "2020-08-08 15:44:41", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("5", "ecomm-unique-shopping-experience", "info", "en_US", "For a shopping experience as unique as your customers", "Product Add-Ons allow your customers to personalize products while they’re shopping on your online store. No more follow-up email requests—customers get what they want, before they’re done checking out. Learn more about this extension that comes included in your plan.", "{}", "pending", "woocommerce.com", "2020-08-08 15:44:41", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("6", "wc-admin-onboarding-email-marketing", "info", "en_US", "Tips, product updates, and inspiration", "We\'re here for you - get tips, product updates and inspiration straight to your email box", "{}", "unactioned", "woocommerce-admin", "2020-10-27 07:38:03", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("7", "wc-admin-marketing-intro", "info", "en_US", "Connect with your audience", "Grow your customer base and increase your sales with marketing tools built for WooCommerce.", "{}", "unactioned", "woocommerce-admin", "2020-10-27 07:38:03", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("8", "wc-admin-mobile-app", "info", "en_US", "Install Woo mobile app", "Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.", "{}", "unactioned", "woocommerce-admin", "2020-11-01 13:30:31", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("9", "wc-admin-usage-tracking-opt-in", "info", "en_US", "Help WooCommerce improve with usage tracking", "Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"http://noithat.lethach.com/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woocommerce.com/usage-tracking\" target=\"_blank\">Read more</a> about what data we collect.", "{}", "unactioned", "woocommerce-admin", "2020-11-03 14:44:35", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("10", "wc-admin-store-notice-giving-feedback-2", "info", "en_US", "Give feedback", "Now that you’ve chosen us as a partner, our goal is to make sure we\'re providing the right tools to meet your needs. We\'re looking forward to having your feedback on the store setup experience so we can improve it in the future.", "{}", "unactioned", "woocommerce-admin", "2020-11-12 17:08:14", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("11", "wc-admin-insight-first-sale", "survey", "en_US", "Did you know?", "A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?", "{}", "unactioned", "woocommerce-admin", "2020-11-12 17:08:14", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("12", "wc-admin-home-screen-feedback", "info", "en_US", "Help us improve the WooCommerce Home screen", "We\'d love your input to shape the future of the WooCommerce Home screen together. Feel free to share any feedback, ideas or suggestions that you have.", "{}", "unactioned", "woocommerce-admin", "2020-11-12 17:08:14", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("13", "wcpay-promo-2020-11", "marketing", "en_US", "Manage subscriber payments from your store\'s dashboard", "Securely accept cards and manage transactions right from your dashboard with <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Payments</a>  – now supporting <a href=\"https://woocommerce.com/products/woocommerce-subscriptions/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Subscriptions</a>! <br /><br /><em>By clicking \"Install now,\" you agree to our <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">Terms of Service</a>.</em>", "{}", "pending", "woocommerce.com", "2020-10-18 11:17:58", NULL, "0", "plain", "", "0", "info");
