INSERT INTO `wp_wc_admin_notes` VALUES("15", "wcpay-promo-2020-12", "marketing", "en_US", "Get 50% off transaction fees with WooCommerce Payments", "Keep more of your hard-earned cash by adding <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_exp20\" target=\"_blank\">WooCommerce Payments</a> to your store. Lock in a discounted rate of 1.5% + $0.15 for $25,000 of payments (or three months, whichever comes first). Limited time offer – don’t miss out! <br /><br /><em>By clicking \"Install now,\" you agree to our promotional <a href=\"https://woocommerce.com/terms-conditions/woocommerce-payments-promotion/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_exp20\" target=\"_blank\">Terms of Service</a>.</em>", "{}", "pending", "woocommerce.com", "2020-12-07 18:47:43", NULL, "0", "plain", "", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("16", "wcpay-subscriptions-2020-12", "marketing", "en_US", "Manage payments from your store\'s dashboard", "Securely accept cards and manage transactions right from your dashboard with <a href=\"https://woocommerce.com/payments/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">WooCommerce Payments</a>! Zero setup fees or monthly fees. Just pay-as-you-go, starting at just 2.9% + $0.30 per transaction for U.S.-issued cards. <br /><br /><em>By clicking \"Install now,\" you agree to our <a href=\"https://wordpress.com/tos/?utm_medium=notification&amp;utm_source=product&amp;utm_campaign=wcpay_ctrl20\" target=\"_blank\">Terms of Service</a>.</em>", "{}", "pending", "woocommerce.com", "2020-12-07 18:47:43", NULL, "0", "plain", "", "0", "info");
